mapper.update:
UPDATE tb_dictionary
SET item_name = #{itemName},
    order_no = #{orderNo},
    reversion = reversion + 1,
    updated_by = #{updatedBy},
    updated_time = #{updatedTime}
WHERE
    id = #{id}
and reversion = #{reversion}

UPDATE tb_dictionary
SET item_name = #{itemName},
delete_flag = #{deleteFlag},
reversion = reversion + 1,
updated_by = #{updatedBy},
updated_time = #{updatedTime}
WHERE
id = #{id}
and reversion = #{reversion}
