mapper.select:
SELECT tfd.id, tfd.uuid, tfd.item_id AS itemId, tfd.cost_money AS costMoney, tfd.cost_type as costType, tfd.note, tfd.created_by AS createdBy, tfd.created_time AS createdTime, tfd.updated_by AS updatedBy, tfd.updated_time AS updatedTime, tfd.reversion, tfd.delete_flag as deleteFlag FROM tb_finance_detail tfd where tfd.delete_flag = 0
and tfd.updated_by = #{userId}
order by tfd.updated_time desc
